import React from 'react';
import { Redirect, Route } from 'react-router-dom';
import { isAuthenticated } from '.';

const AdminRoute = ({ children }) => {
    return (
        <div>
        <Route
            render={() =>
                isAuthenticated() && isAuthenticated().user.role === 1 ? (
                    children
                ) : (
                    <Redirect
                        to={{ pathname: "/signin" }}
                    />
                )
            }
        />
        </div>
    )
}

export default AdminRoute
