import React, { useState, useEffect } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import TodoList from './components/TodoList'
import TodoForm from './components/TodoForm';

function App() {
  const [todos, setTodos] = useState([

  ])
  //add
  const onHandleAdd = (todo) => {
    setTodos([
      ...todos,
      { id: 4, name: todo }
    ])
  }
  // delete
  const onHandleDelete = (id) => {
    const newTodos = todos.filter(item => item.id !== id);
    setTodos(newTodos)
  }
  // 
  const [color, setColor] = useState('yellow')
  const [count, setCount] = useState(0);

  useEffect(() => {
    // call API
    const getTodos = async () => {
      try {
        let response = await fetch('http://localhost:8008')
        let dataTodos = await response.json();
        setTodos(dataTodos)
      } catch (error) {
        console.log(error)
      }
    }
    getTodos();
  }, []);

  return (
    <div className="container">
      <div>
        <p>You clicked {count} times</p>
        <button onClick={() => setCount(count + 1)}>
          Click me
      </button>
      </div>

      <div style={{ background: color, width: "500px", height: "100px" }}>
      </div>
      <TodoForm onAdd={onHandleAdd} />
      <TodoList data={todos} onDelete={onHandleDelete} />

      <button onClick={() => setColor('red')} className="btn btn-info btn-sm">change color</button>
    </div>
  );
}

export default App;
