import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import TodoList from './components/TodoList'

// const todos = [
//   {id:1, name:"to do 1"},  
//   {id:2, name:"to do 2"},
//   {id:3, name:"to do 3"},
// ]

function App() {
  const [todos, setTodos]= useState([
    {id:1, name:"to do 1"},  
    {id:2, name:"to do 2"},
    {id:3, name:"to do 3"}
  ])
  const [color, setColor] = useState('yellow') 
  return (
    <div className="container">
        <div style={{background: color, width: "500px", height: "100px"}}>
        </div>
        <TodoList todos={todos} colors={color}/>

    </div>
  );
}

export default App;
