import React from 'react'
import { useForm } from 'react-hook-form'
import { useHistory } from 'react-router-dom'

const AdminAddUserPage = ({ onAddUser }) => {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const history = useHistory();
    const onHandleSubmit = (data) => {
        const uploads = new FormData();
        uploads.append("name", data.name);
        uploads.append("email", data.email);
        uploads.append("password", data.password);
        onAddUser(uploads);
        // console.log(uploads)
        history.push('/users');
    }

    return (
        <div>
            <h1>User Add</h1>
            <form onSubmit={handleSubmit(onHandleSubmit)}>
                <div className="form-floating mb-3">
                    <input type="text"
                        className="form-control"
                        id="product-name"
                        placeholder="Tên sản phẩm"
                        {...register('name', { required: true })}
                    />
                    <label htmlFor="product-name">Tên nguoi dung</label>
                    {errors.name && <span className="text-danger mt-2">This field is required</span>}
                </div>
                <div className="form-floating mb-3">
                    <input type="text"
                        className="form-control"
                        id="product-category"
                        placeholder="Tên danh mục"
                        {...register('email', { required: true })}
                    />
                    <label htmlFor="product-name">email</label>
                    {errors.name && <span className="text-danger mt-2">This field is required</span>}
                </div>
                <div className="form-floating mb-3">
                    <input type="text"
                        className="form-control"
                        id="product-category"
                        placeholder="Tên danh mục"
                        {...register('password', { required: true })}
                    />
                    <label htmlFor="product-name">password</label>
                    {errors.name && <span className="text-danger mt-2">This field is required</span>}
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default AdminAddUserPage
