import React, { useState, useEffect } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import ProductAPI from './api/productAPI'
import CategoryAPI from './api/categoryAPI'
import Routers from './Routers'

function App() {
  const [products, setProducts] = useState([])
  const [categories, setCategories] = useState([])

  useEffect(() => {
    // call API
    const getProducts = async () => {
      try {
        const { data: products } = await ProductAPI.getAll();
        setProducts(products)
      } catch (error) {
        console.log(error)
      }
    }
    getProducts();
  }, [])
  //add product
  const onHandleAdd = async (product) => {
    try {
      await ProductAPI.add(product)
    } catch (error) {
      console.log(error)
    }
    setProducts([
      ...products,
      product
    ])
  }
  //add cate
  const onHandleAddCate = async (cate) => {
    try {
      await AuthAPI.signup(cate)
    } catch (error) {
      console.log(error)
    }
    setCategories([
      ...categories,
      cate
    ])
  }
  //login

  //edit
  const onHandleEdit = async (product) => {
    try {
      await ProductAPI.update(product)
    } catch (error) {
      console.log(error)
    }
    setProducts([
      ...products,
      product
    ])
  }
  // delete
  const onHandleDelete = async (id) => {
    try {
      await ProductAPI.remove(id);
      const newProducts = products.filter(item => item._id !== id);
      setProducts(newProducts)
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className="container">
      <Routers products={products} onDelete={onHandleDelete} onAdd={onHandleAdd} />
    </div>
  );
}

export default App;
