import React, { useState, useEffect } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ProductAPI from './api/productAPI'
import Routers from './Routers';

function App() {
  const [products, setProducts] = useState([

  ])

  useEffect(() => {
    // call API
    const getProducts = async () => {
      try {
        const { data: products } = await ProductAPI.getAll();
        setProducts(products)
      } catch (error) {
        console.log(error)
      }
    }
    getProducts();
  }, []);
  //add
  const onHandleAdd = async (product) => {
    try {
      await ProductAPI.add(product)
    } catch (error) {
      console.log(error)
    }
    setProducts([
      ...products,
      product
    ])
  }
  // delete
  const onHandleDelete = async (id) => {
    try {
      await ProductAPI.remove(id);
      const newProducts = products.filter(item => item._id !== id);
      setProducts(newProducts)
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className="container">
      <Routers products={products} onDelete={onHandleDelete} onAdd={onHandleAdd}/>
      {/* <div>
        <p>You clicked {count} times</p>
        <button onClick={() => setCount(count + 1)}>
          Click me
      </button>
      </div>

      <div style={{ background: color, width: "500px", height: "100px" }}>
      </div>
      <TodoForm onAdd={onHandleAdd} />
      <TodoList data={products} onDelete={onHandleDelete} />

      <button onClick={() => setColor('red')} className="btn btn-info btn-sm">change color</button> */}
    </div>
  );
}

export default App;
