import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import TodoList from './components/TodoList'
import TodoForm from './components/TodoForm';

function App() {
  const [todos, setTodos]= useState([
    {id:1, name:"to do 1"},  
    {id:2, name:"to do 2"},
    {id:3, name:"to do 3"}
  ])
  // 
  const onHandleAdd = (todo) => {
      console.log(todo)
  }
  // 
  const removeTodo = (id) => {
    const newTodos = todos.filter(item => item.id !== id);
    setTodos(newTodos)
  }

  const [color, setColor] = useState('yellow') 

  return (
    <div className="container">

        <div style={{background: color, width: "500px", height: "100px"}}>
        </div>
        <TodoForm onAdd={onHandleAdd}/>
        <TodoList todos={todos} colors={color}/>

        <button onClick={() => setColor('red')} className="btn btn-info btn-sm">change color</button>
        <button onClick={() => removeTodo(2)} className="btn btn-danger btn-sm">delete to do 2</button>
    </div>
  );
}

export default App;
