import React, { useState, useEffect } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import ProductAPI from './api/productAPI'
import CategoryAPI from './api/categoryAPI'
import Routers from './Routers'

function App() {
  const [products, setProducts] = useState([])
  const [categories, setCategories] = useState([])

  useEffect(() => {
    // call API
    const getProducts = async () => {
      try {
        const { data: products } = await ProductAPI.getAll();
        setProducts(products)
      } catch (error) {
        console.log(error)
      }
    }
    getProducts();
  }, [])
  //
  useEffect(() => {
    // call API
    const getCategories = async () => {
      try {
        const { data: categories } = await CategoryAPI.getAll();
        setCategories(categories)
      } catch (error) {
        console.log(error)
      }
    }
    getCategories();
  }, [])
  //add product
  const onHandleAdd = async (product) => {
    try {
      await ProductAPI.add(product)
    } catch (error) {
      console.log(error)
    }
    setProducts([
      ...products,
      product
    ])
  }
  //add cate
  const onHandleAddCate = async (cate) => {
    try {
      await CategoryAPI.add(cate)
    } catch (error) {
      console.log(error)
    }
    setCategories([
      ...categories,
      cate
    ])
  }
  //edit
  const updateCategory = async (id) => {
    try {
      await CategoryAPI.update(id) 
      const newCate = categories.filter(value => value._id !== id)
    setCategories(newCate)
    } catch (error) {
      console.log(error)
    }

  }
  // delete
  const onHandleDelete = async (id) => {
    try {
      await ProductAPI.remove(id);
      const newProducts = products.filter(value => value._id !== id);
      setProducts(newProducts)
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className="container">
      <Routers products={products} categories={categories} onDelete={onHandleDelete} onAdd={onHandleAdd} 
      onCateAdd={onHandleAddCate} onCateUpdate={updateCategory}/>
    </div>
  );
}

export default App;
