import React, { useState, useEffect } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import TodoList from './components/TodoList'
import TodoForm from './components/TodoForm';
import Routers from './Routers';

function App() {
  const [p, setProducts] = useState([
    {
      "id":1,
      "name":"to do 1"
  },
  {
      "id":2,
      "name":"to do 2"
  },
  {
      "id":3,
      "name":"to do 3"
  }
  ])
  //add
  const onHandleAdd = (todo) => {
    setProducts([
      ...p,
      { id: 4, name: todo }
    ])
  }
  // delete
  const onHandleDelete = (id) => {
    const newp = p.filter(item => item.id !== id);
    setProducts(newp)
  }
  // 
  const [color, setColor] = useState('yellow')
  const [count, setCount] = useState(0);

  useEffect(() => {
    // call API
    const getp = async () => {
      try {
        let response = await fetch('http://localhost:8008')
        let datap = await response.json();
        setProducts(datap)
      } catch (error) {
        console.log(error)
      }
    }
    getp();
  }, []);

  return (
    <div className="container">
      <Routers products={p} onDelete={onHandleDelete}/>
      {/* <div>
        <p>You clicked {count} times</p>
        <button onClick={() => setCount(count + 1)}>
          Click me
      </button>
      </div>

      <div style={{ background: color, width: "500px", height: "100px" }}>
      </div>
      <TodoForm onAdd={onHandleAdd} />
      <TodoList data={p} onDelete={onHandleDelete} />

      <button onClick={() => setColor('red')} className="btn btn-info btn-sm">change color</button> */}
    </div>
  );
}

export default App;
